CREATE TABLE ADDRESS (ADDRESS TEXT, LONGITUDE REAL, LATITUDE REAL, SAVE_TIME TEXT);

CREATE TABLE ORDER_ITEMS (SERIAL_NUMBER TEXT, ORDER_SERIAL_NUMBER TEXT, 
	GOODS_ID INTEGER, GOODS_CODE TEXT, GOODS_NAME TEXT, SHOP_ID INTEGER, 
	UNIT_PRICE REAL, QUANTITY INTEGER, FLAG INTEGER);

CREATE TABLE ORDERS (SERIAL_NUMBER TEXT, BUYER_PHONE TEXT, BUYER_NAME TEXT, 
	AGENT_ID INTEGER, SHOP_ID INTEGER, WORKER_ID INTEGER, STATUS TEXT, SUBMIT_TIME TEXT, 
	COMPLETE_TIME TEXT, PREPARE_TIME INTEGER, DELIVERY_ADDRESS TEXT, 
	DELIVERY_LONGITUDE REAL, DELIVERY_LATITUDE REAL, COMMENT TEXT, COST_OF_RUN_ERRANDS REAL,
	SHOP_NAME TEXT, SHOP_COST_OF_RUN_ERRANDS REAL, SHOP_MIN_CHARGE_FOR_FREE_DELIVERY REAL, SHOP_PHONE TEXT);
